    def controlSystem(self, leButton):
        if (leButton.text() == 'Iniciar' and not self.running):
            leButton.setEnabled(False)
            self.running = True
            theThread = threading.Thread(target=self.definiteMain)
            theThread.start()
        else:
            self.initButton.setEnabled(True)
            self.running = False

    def definiteMain(self):
        while (self.running):

            self.p1.compute()
            self.p2.compute()
            self.p3.compute()
            self.p4.compute()

            self.instP1.appendPlainText(self.p1.inst)
            self.instP2.appendPlainText(self.p2.inst)
            self.instP3.appendPlainText(self.p3.inst)
            self.instP4.appendPlainText(self.p4.inst)

            
        else:
            print('Not Running')

    def setState(self, nState):
        if (nState.text() == "Play"):
            self.state = 1
        else:
            self.state = 0

    def setMode(self, nMode):
        if (nMode.isChecked()):
            if (nMode.text() == 'Continuo'):
                self.mode = 0
            elif (nMode.text() == 'Steps'):
                self.mode = 1
            else:
                self.mode = 2

